import { Component, OnInit } from '@angular/core';
import { BoardCell } from '../models/boardCell';
import { BoardService } from './board.service';
import { X_axic } from '../models/cellPropeties';
import { UnitType } from '../models/chessUnit';

@Component({
  selector: 'app-board',
  templateUrl: './board.component.html',
  styleUrls: ['./board.component.css']
})
export class BoardComponent implements OnInit {

  board : BoardCell[][];

  constructor(private boardService: BoardService){
  }

  ngOnInit() {
    this.board = this.boardService.CreateBoard();
  }

  TestX(x: number) : string{
    return X_axic[x]
  }

  GetUnitImage(cell: BoardCell): string | null{
    if(cell.Unit == null){
      return null;
    }

    var color: string = cell.Unit.Color == 0 ? "White" : "Black"; 
    var type: string = UnitType[cell.Unit.Type]; 

    return `assets/img/${color}_${type}.png`;
  }
}
<div class="container">
    <div *ngFor="let row of board" class="row">
        <div  *ngFor="let item of row" class="cell">
            <!-- {{(item.X)}} / {{item.Y}} -->
            <img [src]="GetUnitImage(item)" />
        </div>
    </div>
</div>
